function mapInit(){lbs.map=new AMap.Map("container",{zoom:12});AMap.service("AMap.Geocoder",function(){lbs.geocoder=new AMap.Geocoder({})});$("#provinceSelect").change(function(){var n=$(this).find("option:selected").val();n&&(lbs.map.setCity(n),lbs.geocoder.setCity(n))});$("#citySelect").change(function(){var n=$(this).find("option:selected").val();n&&(lbs.map.setCity(n),lbs.geocoder.setCity(n))});$("#districtSelect").change(function(){var n=$(this).find("option:selected").val();n&&(lbs.map.setCity(n),lbs.geocoder.setCity(n))})}function getAddressLocation(){lbs.geocoder.getLocation($("#WorkAddress").val(),function(n,t){if(n=="complete"){var i=t.geocodes[0].location,r=t.geocodes[0].formattedAddress;if(lbs.marker!=null)lbs.marker.setPosition(i),lbs.infoWindow.close(),lbs.infoWindow.setContent("施工地址<br>   "+r);else{lbs.marker=new AMap.Marker({map:lbs.map,position:t.geocodes[0].location});lbs.infoWindow=new AMap.InfoWindow({content:"施工地址<br>   "+r,closeWhenClickMap:!0,offset:{x:0,y:-30}});lbs.marker.on("mouseover",function(){lbs.infoWindow.open(lbs.map,lbs.marker.getPosition())})}lbs.map.setCenter(i);$("#WorkLocation").val(i);$("#WorkAddress").removeClass("form-contorl-text-danger")}else $("#WorkAddress").addClass("form-contorl-text-danger")})}var lbs={map:null,geocoder:null,marker:null,infoWindow:null,locationInited:!1};$(document).ready(function(){var n,t=600;$("#WorkAddress").bind("input",function(){window.clearTimeout(n);n=window.setTimeout(function(){getAddressLocation()},t)})});